mapper.select:
SELECT p.project_id AS projectId, p.project_name AS project_name, p.project_stage AS project_stage, p.creation_time AS creation_time, u.username AS creator, o.org_name AS organization FROM mod_projects p LEFT JOIN mod_users u ON p.creator = u.user_id LEFT JOIN organization o ON p.organization_id = o.org_id WHERE p.project_status = 1 AND p.creator = #{creator}
AND ( p.project_name LIKE CONCAT('%', #{keyword}, '%') OR o.org_name LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY p.creation_time DESC
